import React from 'react'
import ReactDOM from 'react-dom'


const Course = (props) =>{
  const {course} = props
  return (
    <li>{course.parts.name} {course.parts.exercises}</li>
  )
}


const App = (props) => {
  const {course} = props

  const courserows = () => course.parts.map(part =>
    <Course
      key={part.id}
      course={course}/>)  

  return (
    <div>
      {courserows()}
    </div>
  )
  }

ReactDOM.render(<App course={course}/>, document.getElementById('root'))
